import React from 'react'
import { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';

export default function Dropdown({ name, list }) {

    const [show, setShow] = useState(false);
    const [Name, setName] = useState(name);

    const LockSelector=useSelector((state)=>state.LockStore);

    const dispatch=useDispatch();
    
    return (
        <div>
            <button className='d-flex align-items-center dropdown_btn' onClick={() => { setShow(!show) }}>
                {Name}
                {
                    show
                        ?
                        <i class="fa fa-caret-up"></i>
                        :
                        <i class="fa fa-caret-down"></i>
                }
            </button>

            <div className={show ? 'show_options' : null}>
                {
                    show
                        ?
                        <ul>
                            {
                                list.map((elem) => {
                                    return (
                                        <li onClick={() => { setName(elem); setShow(false) }}>
                                            {elem}
                                        </li>
                                    )
                                })
                            }
                        </ul>
                        :
                        null
                }
            </div>

        </div>
    )
}
